import React, { Component } from "react";
import Listcards from "./listcards";
import { robots } from "./robots";
import Searchbox from "./searchbox";

class App extends Component {
  constructor() {
    super();
    this.state = {
      robots: robots,
      searchbox: ""
    };
  }

  onSearchchange
  render() {
    return (
      <div className="tc">
        <h1>RoboFriends</h1>
        <Searchbox />
        <Listcards robots={this.state.robots} />
      </div>
    );
  }
}

export default App;
