import React from "react";

const Card = ({ name, email, id }) => {
  return (
    <div className="tc bg-light-green dib br3 pa3 mb3 mr4 grow bw2 shadow-5">
      <img src={`https://robohash.org/${id}?200x200`} alt="robot"></img>
      <div>
        <h2>{name}</h2>
        <p>{email}</p>
      </div>
    </div>
  );
};

export default Card;
